(ns year2015.day01
  (:require [ysera.test :refer [is is=]]))

; https://adventofcode.com/2015/day/1

;(()) and ()() both result in floor 0.
;((( and (()(()( both result in floor 3.
;))((((( also results in floor 3.
;()) and ))( both result in floor -1 (the first basement level).
;))) and )())()) both result in floor -3.
(defn part1
  {:test
   (fn []
     (is= (part1 "(())") 0)
     (is= (part1 "()()") 0)
     (is= (part1 "(((") 3)
     (is= (part1 "(()(()(") 3)
     (is= (part1 "))(((((") 3)
     (is= (part1 "())") -1)
     (is= (part1 "))(") -1)
     (is= (part1 ")))") -3)
     (is= (part1 ")())())") -3)
     (is= (part1 " ( ) ") 0)
     )}
  [string]
  (reduce
    (fn [acc ch]
      (condp = ch
        \) (dec acc)
        \( (inc acc)
        acc
        ))
    0 string
    ))

;) causes him to enter the basement at character position 1.
;()()) causes him to enter the basement at character position 5.
(defn part2
  {:test (fn []
           (is= (part2 ")") 1)
           (is= (part2 "()())") 5)
           )}
  [string]
  (as-> string $
        (map-indexed (fn [idx ch] [idx ch]) $)
        (loop [acc 0
               [item & items] $]
          (let [acc (condp = (second item)
                      \) (dec acc)
                      \( (inc acc)
                      acc)]
            (if (= acc -1)
              (inc (first item))
              (recur acc items))
            ))))

