(ns year2021.day03.day3
  (:require [ysera.test :refer [is is= is-not]]
            [clojure.string :as str :only [index-of]]))

; https://adventofcode.com/2021/day/3

(defn report2matrix
  {:doc "Converts report from string format to matrix of numerics"
   :test (fn []
           (is= (report2matrix "11\n00\n11") [[1 1] [0 0] [1 1]])
           )}
  [str]
  (->> str
       (str/split-lines)
       (map vec)
       (map #(map (fn [x] (- (int x) (int \0))) %1) )
       )
  )

(defn most-common-bit
  {:test (fn []
           (is= (most-common-bit [1 0 1 1 1]) 1)
           (is= (most-common-bit [1 0 0 1 0]) 0)
           )
   }
  [bits]
  (if (pos? (-
              (count (filter #(= 1 %) bits))
              (count (filter #(= 0 %) bits))
              )) 1 0)
  )

(defn least-common-bit
  {:test (fn []
           (is= (least-common-bit [1 0 1 1 1]) 0)
           (is= (least-common-bit [1 0 0 1 0]) 1)
           )
   }
  [bits]
  (- 1 (most-common-bit bits))
  )

(defn transpose [m]
  (apply mapv vector m))

(defn bin2dec [n]
  (read-string (str "2r" n))
  )

(defn power-consumtion
  {:doc "Gamma  rate (most common bits) times epsilon rate (least common bits)"
   :test (fn []
           (is= (power-consumtion [[1 0]
                                   [1 0]
                                   [0 1]]) (* (bin2dec "10") (bin2dec "01")))
           )
   }
  [report-matrix]
  (let [bits-matrix (transpose report-matrix)
        gamma-rate (bin2dec(apply str (map most-common-bit bits-matrix)))
        epsilon-rate (bin2dec(apply str (map least-common-bit bits-matrix)))]
        (* gamma-rate epsilon-rate))
  )

(defn part1
  [nums]
  (->> nums
       (report2matrix)
       (power-consumtion)
       )
  )

